mapper.update:
UPDATE k2_sw_paymentCheck
	SET
		paymentStateCode=#{servicePaymentCheck}
	WHERE 
		paymentCode=#{paymentStateCode}

mapper.insert:
INSERT INTO k2_sw_paymentCheck(
	paymentCheckCode
	,contractManageCode
	,paymentCode
	,clientId
	,paymentStateCode
	,paymentEstimate
	,paymentCharge
	,paymentBalance
	,paymentType
	,paymentDate
)VALUES (
(SELECT 
	MAX(CAST(paymentCheckCode AS INTEGER)+1)
FROM 
	k2_sw_paymentCheck AS z)
	,#{contractManageCode}
	,#{paymentCode}
	,'shopid001'
	,'payWaiting'
	,#{paymentEstimate}
	,#{paymentCharge}
	,#{paymentBalance}
	,#{paymentType}
	,NOW()
	);
