mapper.insert:
	INSERT INTO k2_sw_contractRequest
		(contractManageCode
		,mainBusinessCode
		,serviceCode
		,serviceAmount
		,servicePeriod
		,serviceTotalPrice
		,slipNumber
		,serviceRegistrationDate
		,serviceEndDate
		,serviceRequestDate
		,serviceRequestStatus
		,serviceApprovalDate)
	VALUES( 
		(SELECT CONCAT('estimate',LPAD(max(SUBSTRING((contractManageCode),10,2))+1,2,'0'))
	FROM
  	 k2_sw_contractRequest ALIAS_FOR_SUBQUERY) 
			 ,#{mainBusinessCode}
			 ,#{serviceCode}
			 ,#{serviceAmount}
			 ,#{servicePeriod}
			 ,#{serviceTotalPrice}
			 ,''
			 ,#{serviceRegistrationDate}
			 ,#{serviceEndDate}
			 ,NOW()
			 ,'신청'
			 ,0
			 );
